import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, makeStyles, Grid } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  form: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    display: 'flex',
    alignItems: 'center',
  },
  textField: {
    marginRight: theme.spacing(2),
    flex: 1,
  },
  button: {
    marginLeft: theme.spacing(2),
  },
  weatherContainer: {
    marginTop: theme.spacing(4),
  },
  weatherItem: {
    padding: theme.spacing(2),
    border: '1px solid #ccc',
    borderRadius: theme.spacing(1),
  },
}));

const WeatherApp = () => {
  const classes = useStyles();
  const [location, setLocation] = useState('');
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setForecast(response.data.forecast);
      setError('');
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  // Function to convert temperature from Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  // Function to aggregate max and min temperatures for each day
  const aggregateDailyTemperatures = (list) => {
    const dailyTemperatures = {};
    list.forEach((item) => {
      const date = new Date(item.dt * 1000).toISOString().split('T')[0];
      if (!dailyTemperatures[date]) {
        dailyTemperatures[date] = { max: -Infinity, min: Infinity };
      }
      if (item.temp && item.temp.max !== undefined && item.temp.min !== undefined) {
        dailyTemperatures[date].max = Math.max(dailyTemperatures[date].max, item.temp.max);
        dailyTemperatures[date].min = Math.min(dailyTemperatures[date].min, item.temp.min);
      }
    });
    return dailyTemperatures;
  };

  return (
    <Container maxWidth="md">
      <Typography variant="h3" gutterBottom align="center">Weather App</Typography>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
        }}
        className={classes.form}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          className={classes.textField}
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          className={classes.button}
        >
          Get Weather
        </Button>
      </form>
      {error && <Typography color="error" align="center" className={classes.weatherItem}>{error}</Typography>}
      {currentWeather && (
         <div className={classes.weatherContainer}>
           <Typography variant="h5" gutterBottom align="center">Current Weather</Typography>
           <div className={classes.weatherItem}>
             <Typography align="center">Temperature: {kelvinToCelsius(currentWeather.main.temp).toFixed(2)}°C</Typography>
             <Typography align="center">Description: {currentWeather.weather[0].description}</Typography>
           </div>
         </div>
       )}
      {forecast && (
        <div className={classes.weatherContainer}>
          <Typography variant="h5" gutterBottom align="center">5-Day Weather Forecast</Typography>
          <Grid container spacing={3} justify="center">
            {Object.entries(aggregateDailyTemperatures(forecast.list)).map(([date, temperatures]) => (
              <Grid item key={date}>
                <div className={classes.weatherItem}>
                  <Typography align="center" variant="h6">{new Date(date).toLocaleDateString('en-US', { weekday: 'short' })}</Typography>
                  <Typography align="center">{date}</Typography>
                  <Typography align="center">Max: {kelvinToCelsius(temperatures.max).toFixed(2)}°C</Typography>
                  <Typography align="center">Min: {kelvinToCelsius(temperatures.min).toFixed(2)}°C</Typography>
                </div>
              </Grid>
            ))}
          </Grid>
        </div>
      )}
    </Container>
  );
};

export default WeatherApp;
