import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography } from '@material-ui/core';

const WeatherApp = () => {
  const [location, setLocation] = useState('');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setCurrentWeather(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching current weather data:', error);
      setError('Failed to fetch current weather data. Please try again.');
    }
  };

  const fetchForecastData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/forecast?location=${location}`);
      setForecast(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching forecast data:', error);
      setError('Failed to fetch forecast data. Please try again.');
    }
  };

  // Function to convert temperature from Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h3" gutterBottom>Weather App</Typography>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
          fetchForecastData();
        }}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          style={{ marginTop: '10px' }}
        >
          Get Weather
        </Button>
      </form>
      {error && <Typography color="error">{error}</Typography>}
      {currentWeather && (
        <div>
          <Typography variant="h5" gutterBottom>Current Weather</Typography>
          <Typography>Temperature: {kelvinToCelsius(currentWeather.main.temp).toFixed(2)}°C</Typography>
          <Typography>Description: {currentWeather.weather[0].description}</Typography>
          {/* Include additional weather information here */}
        </div>
      )}
      {forecast && (
        <div>
          <Typography variant="h5" gutterBottom>5-Day Weather Forecast</Typography>
          {forecast.daily.map((day, index) => (
            <div key={index}>
              <Typography>Date: {new Date(day.dt * 1000).toLocaleDateString()}</Typography>
              <Typography>Temperature: {kelvinToCelsius(day.temp.day).toFixed(2)}°C</Typography>
              <Typography>Description: {day.weather[0].description}</Typography>
              {/* Include additional forecast information here */}
            </div>
          ))}
        </div>
      )}
    </Container>
  );
};

export default WeatherApp;
