import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  form: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    display: 'flex',
    alignItems: 'center',
  },
  textField: {
    marginRight: theme.spacing(2),
    flex: 1,
  },
  button: {
    marginLeft: theme.spacing(2),
  },
  weatherContainer: {
    marginTop: theme.spacing(4),
  },
  weatherItem: {
    marginBottom: theme.spacing(2),
  },
}));

const WeatherApp = () => {
  const classes = useStyles();
  const [location, setLocation] = useState('');
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setForecast(response.data.forecast);
      setError('');
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  // Function to convert temperature from Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h3" gutterBottom align="center">Weather App</Typography>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
        }}
        className={classes.form}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          className={classes.textField}
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          className={classes.button}
        >
          Get Weather
        </Button>
      </form>
      {error && <Typography color="error" align="center" className={classes.weatherItem}>{error}</Typography>}
      {forecast && (
        <div className={classes.weatherContainer}>
          <Typography variant="h5" gutterBottom align="center">5-Day Weather Forecast</Typography>
          {forecast.list.map((day, index) => (
            <div key={index} className={classes.weatherItem}>
              <Typography align="center">Date: {new Date(day.dt * 1000).toLocaleDateString()}</Typography>
              {day.temp && (
                <React.Fragment>
                  <Typography align="center">Max Temperature: {day.temp.max ? kelvinToCelsius(day.temp.max).toFixed(2) : 'N/A'}°C</Typography>
                  <Typography align="center">Min Temperature: {day.temp.min ? kelvinToCelsius(day.temp.min).toFixed(2) : 'N/A'}°C</Typography>
                </React.Fragment>
              )}
              <Typography align="center">Description: {day.weather && day.weather[0] ? day.weather[0].description : 'N/A'}</Typography>
            </div>
          ))}
        </div>
      )}
    </Container>
  );
};

export default WeatherApp;
