import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button } from '@material-ui/core';

const WeatherApp = () => {
  const [location, setLocation] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setWeatherData(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  const fetchForecastData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/forecast?location=${location}`);
      setForecastData(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching forecast data:', error);
      setError('Failed to fetch forecast data. Please try again.');
    }
  };

  // Function to convert temperature from Kelvin to Celsius
  const kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  return (
    <Container maxWidth="sm">
      <h1>Weather App</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
          fetchForecastData(); // Fetch forecast data when submitting the form
        }}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          style={{ marginTop: '10px' }}
        >
          Get Weather
        </Button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {weatherData && (
        <div>
          <h2>Current Weather</h2>
          <p>Temperature: {kelvinToCelsius(weatherData.main.temp).toFixed(2)}°C</p>
          <p>Description: {weatherData.weather[0].description}</p>
          {/* Additional weather information */}
        </div>
      )}
      {forecastData && (
        <div>
          <h2>5-Day Forecast</h2>
          {forecastData.list.map((forecast, index) => (
            <div key={index}>
              <p>Date: {forecast.dt_txt}</p>
              <p>Temperature: {kelvinToCelsius(forecast.main.temp).toFixed(2)}°C</p>
              <p>Description: {forecast.weather[0].description}</p>
              {/* Additional forecast information */}
            </div>
          ))}
        </div>
      )}
    </Container>
  );
};

export default WeatherApp;
