      <Typography variant="h3" gutterBottom align="center">Weather App</Typography>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
        }}
        className={classes.form}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          className={classes.textField}
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          className={classes.button}
        >
          Get Weather
        </Button>
        <Button
          variant="contained"
          color="secondary"
          onClick={saveFavorite}
          disabled={!location.trim()}
          className={classes.button}
        >
          Add to Favorites
        </Button>
      </form>
      {error && <Typography color="error" align="center" className={classes.weatherItem}>{error}</Typography>}
