import React, { useState } from 'react';
import axios from 'axios';
import Form from './components/Form';
import WeatherDisplay from './components/WeatherDisplay';
import ForecastDisplay from './components/ForecastDisplay';


const App = () => {
  const [weatherData, setWeatherData] = useState(null); // Define weatherData state
  const [forecastData, setForecastData] = useState(null); // Define forecastData state

  const fetchWeatherData = async (location) => {
    try {
      const response = await axios.get(`/weather?location=${location}`);
      setWeatherData(response.data); // Update weatherData state with fetched data
      // Fetch forecast data using another API endpoint
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Weather Application</h1>
      <Form onSubmit={fetchWeatherData} />
      {weatherData && <WeatherDisplay weatherData={weatherData} />}
      {forecastData && <ForecastDisplay forecastData={forecastData} />}
    </div>
  );
};

export default App;
