import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, makeStyles, Grid, CircularProgress } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  form: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    display: 'flex',
    alignItems: 'center',
  },
  textField: {
    marginRight: theme.spacing(2),
    flex: 1,
  },
  button: {
    marginLeft: theme.spacing(2),
  },
  weatherContainer: {
    marginTop: theme.spacing(4),
  },
  weatherItem: {
    padding: theme.spacing(2),
    border: '1px solid #ccc',
    borderRadius: theme.spacing(1),
  },
  loadingIndicator: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: theme.spacing(4),
  },
}));

const WeatherApp = () => {
  const classes = useStyles();
  const [location, setLocation] = useState('');
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState('');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [temperatureUnit, setTemperatureUnit] = useState('Celsius'); // Default to Celsius

  const fetchWeatherData = async () => {
    if (!location.trim()) {
      setError('Please enter a location.');
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setCurrentWeather(response.data.currentWeather);
      setForecast(response.data.forecast);
      setError('');
      setLoading(false);
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
      setLoading(false);
    }
  };

  const kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  const kelvinToFahrenheit = (kelvin) => {
    return (kelvin * 9) / 5 - 459.67;
  };

  const toggleTemperatureUnit = () => {
    setTemperatureUnit(prevUnit => prevUnit === 'Celsius' ? 'Fahrenheit' : 'Celsius');
  };

  const aggregateDailyTemperatures = (list) => {
    const dailyTemperatures = {};
    const currentDate = new Date();
    const nextFiveDays = new Date(currentDate);
    nextFiveDays.setDate(nextFiveDays.getDate() + 5);

    list.forEach((item) => {
      const forecastDate = new Date(item.dt * 1000);
      if (forecastDate <= nextFiveDays) {
        const date = forecastDate.toISOString().split('T')[0];
        if (!dailyTemperatures[date]) {
          dailyTemperatures[date] = { max: -Infinity, min: Infinity };
        }
        if (item.temp && typeof item.temp.max === 'number' && typeof item.temp.min === 'number') {
          dailyTemperatures[date].max = Math.max(dailyTemperatures[date].max, item.temp.max);
          dailyTemperatures[date].min = Math.min(dailyTemperatures[date].min, item.temp.min);
        }
      }
    });

    // Convert max and min temperatures to 'N/A' if not available or valid
    Object.values(dailyTemperatures).forEach((temperatures) => {
      temperatures.max = isValidTemperature(temperatures.max) ? `${kelvinToCelsius(temperatures.max).toFixed(2)}°C` : 'N/A';
      temperatures.min = isValidTemperature(temperatures.min) ? `${kelvinToCelsius(temperatures.min).toFixed(2)}°C` : 'N/A';
    });

    return dailyTemperatures;
  };

  const isValidTemperature = (temperature) => {
    return typeof temperature === 'number' && !isNaN(temperature) && isFinite(temperature);
  };



  return (
    <Container maxWidth="md">
      <Typography variant="h3" gutterBottom align="center">Weather App</Typography>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
        }}
        className={classes.form}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          className={classes.textField}
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          className={classes.button}
        >
          Get Weather
        </Button>
      </form>
      {error && <Typography color="error" align="center" className={classes.weatherItem}>{error}</Typography>}
      {loading && (
        <div className={classes.loadingIndicator}>
          <CircularProgress />
        </div>
      )}
      {currentWeather && (
        <div className={classes.weatherContainer}>
          <Typography variant="h5" gutterBottom align="center">Current Weather</Typography>
          <div className={classes.weatherItem}>
            <Typography align="center">Temperature: {temperatureUnit === 'Celsius' ? kelvinToCelsius(currentWeather.main.temp).toFixed(2) + '°C' : kelvinToFahrenheit(currentWeather.main.temp).toFixed(2) + '°F'}</Typography>
            <Typography align="center">Description: {currentWeather.weather[0].description}</Typography>
          </div>
        </div>
      )}
      {forecast && (
        <div className={classes.weatherContainer}>
          <Typography variant="h5" gutterBottom align="center">5-Day Weather Forecast</Typography>
          <Grid container spacing={3} justify="center">
            {Object.entries(aggregateDailyTemperatures(forecast.list)).map(([date, temperatures]) => (
              <Grid item key={date}>
                <div className={classes.weatherItem}>
                  <Typography align="center" variant="h6">{new Date(date).toLocaleDateString('en-US', { weekday: 'short' })}</Typography>
                  <Typography align="center">{date}</Typography>
                  <Typography align="center">Max: {temperatureUnit === 'Celsius' ? kelvinToCelsius(temperatures.max).toFixed(2) + '°C' : kelvinToFahrenheit(temperatures.max).toFixed(2) + '°F'}</Typography>
                  <Typography align="center">Min: {temperatureUnit === 'Celsius' ? kelvinToCelsius(temperatures.min).toFixed(2) + '°C' : kelvinToFahrenheit(temperatures.min).toFixed(2) + '°F'}</Typography>
                </div>
              </Grid>
            ))}
          </Grid>
        </div>
      )}
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <Button variant="outlined" color="primary" onClick={toggleTemperatureUnit}>
          Toggle Temperature Unit ({temperatureUnit})
        </Button>
      </div>
    </Container>
  );
};

export default WeatherApp;
