import React, { useState } from 'react';
import axios from 'axios';

// Import necessary UI components from a design system (e.g., Material UI, Chakra UI, or Ant Design)
import { Container, TextField, Button } from '@material-ui/core';

const WeatherApp = () => {
  const [location, setLocation] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setWeatherData(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
    }
  };

  return (
    <Container maxWidth="sm">
      <h1>Weather App</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
        }}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          style={{ marginTop: '10px' }}
        >
          Get Weather
        </Button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {weatherData && (
        <div>
          <h2>Current Weather</h2>
          <p>Temperature: {kelvinToCelsius(weatherData.main.temp).toFixed(2)}Â°C</p>
          <p>Description: {weatherData.weather[0].description}</p>
          {/* Include forecast information here */}
        </div>
      )}
    </Container>
  );
};

export default WeatherApp;
