import React, { useState } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, makeStyles, Grid, CircularProgress, Switch } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  form: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    display: 'flex',
    alignItems: 'center',
  },
  textField: {
    marginRight: theme.spacing(2),
    flex: 1,
  },
  button: {
    marginLeft: theme.spacing(2),
  },
  weatherContainer: {
    marginTop: theme.spacing(4),
  },
  weatherItem: {
    padding: theme.spacing(2),
    border: '1px solid #ccc',
    borderRadius: theme.spacing(1),
  },
  loadingIndicator: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: theme.spacing(4),
  },
}));

const WeatherApp = () => {
  const classes = useStyles();
  const [location, setLocation] = useState('');
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState('');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [unit, setUnit] = useState('celsius'); // default unit is Celsius

  const fetchWeatherData = async () => {
    if (!location.trim()) {
      setError('Please enter a location.');
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5001/weather?location=${location}`);
      setCurrentWeather(response.data.currentWeather);
      setForecast(response.data.forecast);
      setError('');
      setLoading(false);
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
      setLoading(false);
    }
  };

  const kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  const kelvinToFahrenheit = (kelvin) => {
    return (kelvin - 273.15) * 9/5 + 32;
  };

  const handleUnitChange = () => {
    setUnit(unit === 'celsius' ? 'fahrenheit' : 'celsius');
  };

  const renderTemperature = (temperature) => {
    if (unit === 'celsius') {
      return kelvinToCelsius(temperature).toFixed(2) + '째C';
    } else {
      return kelvinToFahrenheit(temperature).toFixed(2) + '째F';
    }
  };

  return (
    <Container maxWidth="md">
      <Typography variant="h3" gutterBottom align="center">Weather App</Typography>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          fetchWeatherData();
        }}
        className={classes.form}
      >
        <TextField
          label="Enter city or coordinates"
          variant="outlined"
          fullWidth
          className={classes.textField}
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit"
          className={classes.button}
        >
          Get Weather
        </Button>
      </form>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
        <Typography variant="body1" style={{ marginRight: '10px' }}>Celsius</Typography>
        <Switch checked={unit === 'fahrenheit'} onChange={handleUnitChange} color="primary" />
        <Typography variant="body1" style={{ marginLeft: '10px' }}>Fahrenheit</Typography>
      </div>
      {error && <Typography color="error" align="center" className={classes.weatherItem}>{error}</Typography>}
      {loading && (
        <div className={classes.loadingIndicator}>
          <CircularProgress />
        </div>
      )}
      {currentWeather && (
        <div className={classes.weatherContainer}>
          <Typography variant="h5" gutterBottom align="center">Current Weather</Typography>
          <div className={classes.weatherItem}>
            <Typography align="center">Temperature: {renderTemperature(currentWeather.main.temp)}</Typography>
            <Typography align="center">Description: {currentWeather.weather[0].description}</Typography>
          </div>
        </div>
      )}
      {forecast && (
        <div className={classes.weatherContainer}>
          <Typography variant="h5" gutterBottom align="center">5-Day Weather Forecast</Typography>
          <Grid container spacing={3} justify="center">
            {Object.entries(aggregateDailyTemperatures(forecast.list)).map(([date, temperatures]) => (
              <Grid item key={date}>
                <div className={classes.weatherItem}>
                  <Typography align="center" variant="h6">{new Date(date).toLocaleDateString('en-US', { weekday: 'short' })}</Typography>
                  <Typography align="center">{date}</Typography>
                  <Typography align="center">Max: {renderTemperature(temperatures.max)}</Typography>
                  <Typography align="center">Min: {renderTemperature(temperatures.min)}</Typography>
                </div>
              </Grid>
            ))}
          </Grid>
        </div>
      )}
    </Container>
  );
};

export default WeatherApp;
